let input=document.getElementById("input"),btn=document.getElementById("btn"),result=document.querySelector(".result .container");function getInputVal(){fetchData(`https://api.openweathermap.org/data/2.5/weather?q=${input.value.toLowerCase()}%2Cegypt&appid=e36ed364400282e43250b6c4c0274d44&units=metric`)}async function fetchData(e){try{let t=await fetch(e),n=await t.json();result.innerHTML="";let a=document.createElement("div");a.className="weather";let d=document.createElement("h1"),c=document.createTextNode(`${n.name}, ${n.sys.country}`);d.appendChild(c);let o=document.createElement("div"),l=document.createElement("p"),r=document.createTextNode(`Temperature: ${Math.floor(n.main.temp)}°`);l.appendChild(r);let i=document.createElement("p"),p=document.createTextNode(`Humidity: ${Math.floor(n.main.humidity)}%`);i.appendChild(p);let m=document.createElement("p"),u=document.createTextNode(`Max Temperature: ${Math.floor(n.main.temp_max)}°`);m.appendChild(u);let h=document.createElement("p"),s=document.createTextNode(`Min Temperature: ${Math.floor(n.main.temp_min)}°`);h.appendChild(s),a.appendChild(d),o.appendChild(l),o.appendChild(i),o.appendChild(h),o.appendChild(m),a.appendChild(o),result.appendChild(a)}catch(e){result.innerHTML="";let t=document.createElement("div");t.className="error";let n=document.createElement("h1"),a=document.createTextNode("Sorry the weather failed to fetch. Please try again or check if the city name is correct");n.appendChild(a),t.appendChild(n),result.appendChild(t),console.log(`Reason: ${e}`)}}btn.addEventListener("click",getInputVal);let up=document.querySelector(".up");window.onscroll=function(){window.scrollY>=600?up.style.cssText="opacity: 1; z-index: 1000;":up.style.cssText="opacity: 0; z-index: -1000;"},up.onclick=function(){window.scrollTo({left:0,top:0,behavior:"smooth"})};